import { useRef } from "react";

const useCacheCheckList = ()=> {
    const refUnCheck = useRef(new Set());
    const refChecked = useRef(new Set());

    const addCheck = (id: string)=> {
        refChecked.current.add(id);
        if(refUnCheck.current.has(id)) {
            refUnCheck.current.delete(id);
        }
    }
    const deleteCheck = (id: string)=> {
        if(refChecked.current.has(id)) {
            refChecked.current.delete(id);
        }
        refUnCheck.current.add(id)
    }
    return {unCheckList: refUnCheck.current, checkedList: refChecked.current, addCheck, deleteCheck};
}
export default useCacheCheckList;
